INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15301, 26950, 'Pasireotide', 'Bradycardia', 'Bradycardia has been reported with the use of somatostatin analogs.  It is recommended to carefully monitor patients with cardiac disorders for the development of bradycardia.  Dose adjustments of beta-blockers, calcium channel blockers, or correction of electrolyte disturbances may be necessary.  Care should be taken when initiating treatment with these agents in patients with bradycardia.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15302, 26948, 'Pasireotide', 'Cholelithiasis', 'Somatostatin analogs may reduce gallbladder motility and lead to gallstones formation.  It is recommended to assess the gallbladder prior to starting therapy and periodically thereafter.', '2', '', 'DDInter', 0);
