INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15312, 20291, 'Pegfilgrastim', 'Leukocytosis', 'Filgrastim and pegfilgrastim are growth factors for neutrophil progenitor cells.  Therefore, these should not be used in patients with leukocytosis.  If excessive white blood cell counts occur during CSF therapy, treatment should be interrupted or the dosage reduced.', '3', '', 'DDInter', 0);
