INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15349, 14520, 'Avanafil', 'Priapism', 'Prolonged erection greater than 4 hours and priapism (painful erections greater than 6 hours) have been reported during treatment with phosphodiesterase-5 (PDE 5) inhibitors.  Priapism may result in penile tissue damage and permanent loss of potency if not treated promptly.  These agents should be used cautiously in patients with conditions that may predispose them to priapism such as sickle cell anemia, multiple myeloma, or leukemia, and those with anatomical deformation of the penis (such as angulation, cavernosal fibrosis, or Peyronie''s disease).  If an erection persists longer than 4 hours, the patient should seek immediate medical assistance.', '2', '', 'DDInter', 0);
