INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15382, 14520, 'Avanafil', 'Liver Diseases', 'Phosphodiesterase 5 (PDE-5) inhibitors are cleared predominantly by hepatic metabolism.  The pharmacokinetic disposition of these agents has not been assessed in patients with severe hepatic impairment.  No dosage modification is recommended for patients with mild to moderate hepatic impairment, however, therapy with these agents should not be administered to patients with severe hepatic impairment.  In patients with mild hepatic impairment a lower dose of these agents should be used as initial therapy.', '2', '', 'DDInter', 0);
