INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15722, 9007, 'Pentoxifylline', 'Cardiomyopathies', 'Although pentoxifylline has been used safely to treat peripheral arterial disease in patients with concurrent coronary artery and cerebrovascular diseases, there have been occasional reports of angina, arrhythmia, and hypotension.  Controlled trials do not show a causal relationship; however, since pentoxifylline is a methylxanthine derivative, the possibility of these adverse effects should be considered.  Therapy with pentoxifylline should be administered cautiously in patients with arrhythmia, coronary artery and/or cerebrovascular insufficiency.', '2', '', 'DDInter', 0);
