INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15776, 11667, 'Perphenazine', 'Anticholinergic Syndrome', 'Phenothiazines have anticholinergic activity, to which elderly patients are particularly sensitive.  Low-potency agents such as chlorpromazine and thioridazine tend to exhibit greater anticholinergic effects than other agents in the class.  Therapy with phenothiazines should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.', '2', '', 'DDInter', 0);
