INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15791, 11667, 'Perphenazine', 'Parkinsonian Disorders', 'The use of phenothiazines is associated with pseudo-parkinsonian symptoms such as akinesia, bradykinesia, tremors, pill-rolling motion, cogwheel rigidity, and postural abnormalities including stooped posture and shuffling gait.  The onset is usually 1 to 2 weeks following initiation of therapy or an increase in dosage.  Propylamino derivatives such as chlorpromazine, promazine, and triflupromazine may be more likely to induce these effects.  Therapy with phenothiazines should be administered cautiously in patients with Parkinson''s disease or parkinsonian symptoms.', '2', '', 'DDInter', 0);
