INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15810, 29604, 'Pazopanib', 'Peptic Ulcer Perforation', 'Inhibitors of vascular endothelial growth factor receptors (VEGFR) may cause gastrointestinal perforation and fistulae.  Avoid use of these agents in patients with ovarian cancer who have evidence of recto-sigmoid involvement by pelvic examination or bowel involvement on CT scan or clinical symptoms of bowel obstruction.  Permanently discontinue these agents in patients with gastrointestinal perforation.', '3', '', 'DDInter', 0);
