INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (15882, 206, 'Perindopril', 'Hepatic Insufficiency', 'The bioavailability of perindoprilat, the active moiety of perindopril is increased in patients with impaired hepatic function.  Plasma concentrations of perindoprilat in patients with impaired liver function were about 50% higher than those observed in healthy subjects or hypertensive patients with normal liver function.  Caution should be exercised when using perindopril in these patients.', '2', '', 'DDInter', 0);
