INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16024, 4480, 'Phenylephrine (topical)', 'Prostatic Hyperplasia', 'Topically applied sympathomimetic agents are systemically absorbed, with the potential for producing clinically significant systemic effects, particularly during prolonged or indiscriminate use.  In patients with prostate enlargement, urinary difficulty may develop or worsen due to smooth muscle contraction in the bladder neck via stimulation of alpha-1 adrenergic receptors.  Therapy with topical sympathomimetic agents should be administered cautiously in patients with hypertrophy or neoplasm of the prostate.  It is important that the recommended dosages of the individual products not be exceeded.', '2', '', 'DDInter', 0);
