INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16049, 4480, 'Phenylephrine', 'Glaucoma', 'Sympathomimetic agents can induce transient mydriasis via stimulation of alpha-1 adrenergic receptors.  In patients with anatomically narrow angles or narrow-angle glaucoma, pupillary dilation can provoke an acute attack.  In patients with other forms of glaucoma, mydriasis may occasionally increase intraocular pressure.  Therapy with sympathomimetic agents should be administered cautiously in patients with or predisposed to glaucoma, particularly narrow-angle glaucoma.', '2', '', 'DDInter', 0);
