INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16123, 2177, 'Phenytoin', 'Kidney Diseases', 'The plasma protein binding of phenytoin may be significantly decreased in patients with renal impairment, resulting in elevated free drug concentrations and increased risk of toxicity.  This effect is proportional to the degree of renal impairment and stems from quantitative differences in serum albumin as well as qualitative differences in the ability to bind phenytoin.  Therapy with phenytoin should be administered cautiously in patients with impaired renal function.  Both the therapeutic and toxic plasma total phenytoin levels may be lower than normal in these patients and should be considered in dosing.  Alternatively, the monitoring of unbound phenytoin concentrations may be appropriate.', '3', '', 'DDInter', 0);
