INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16199, 2177, 'Phenytoin', 'Hyperglycemia', 'Phenytoin, particularly in high dosages, may cause hyperglycemia by inhibiting insulin release.  The drug may also raise serum glucose levels in diabetic patients.  Therapy with phenytoin should be administered cautiously in patients with diabetes mellitus, glucose intolerance, or a predisposition to hyperglycemia.  Patients with diabetes mellitus should be monitored more closely during phenytoin therapy, and their antidiabetic regimen adjusted accordingly.  The same precautions should also be observed with other hydantoin anticonvulsants (i.e. ethotoin and mephenytoin) because of their structural and pharmacological similarities to phenytoin.', '2', '', 'DDInter', 0);
