INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16366, 4073, 'Pimecrolimus (topical)', 'IMMUNE SUPPRESSION', 'The use of calcineurin inhibitors as pimecrolimus and tacrolimus topical should be avoided in immunocompromised adults and children, including patients on systemic immunosuppressive medications.  Although causal relationship has not been established, there is a concern about a potential risk of infections, lymphomas and skin malignancies when using topical calcineurin inhibitors for a prolonged time.', '3', '', 'DDInter', 0);
