INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16370, 4073, 'Pimecrolimus (topical)', 'Skin Neoplasms', 'The use of calcineurin inhibitors such as pimecrolimus and tacrolimus topical should be avoided on malignant or premalignant skin conditions.  Some of these conditions might present as dermatitis.  Calcineurin inhibitors should also be avoided in any skin conditions where there is the potential for increased systemic absorption of the medication as when there is a skin barrier defect.', '2', '', 'DDInter', 0);
