INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16374, 4411, 'Pilocarpine (ophthalmic)', 'Retinal Detachment', 'The use of miotic agents may occasionally cause retinal detachment due to drug-induced ciliary or accommodative spasm, which causes the lens and vitreous to move forward and create a retinal tear.  Therapy with miotic agents should be administered with extreme caution, if at all, in patients with risk factors for retinal detachment, such as old age, retinal degenerative changes or other retinal disorders, aphakia, prior cataract extraction, or a history of severe myopia or retinal detachment.', '3', '', 'DDInter', 0);
