INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16404, 20843, 'Axitinib', 'Heart Failure', 'Cardiac failure, including fatal events have been reported in patients receiving axitinib.  It is recommended to monitor for signs or symptoms of cardiac failure throughout treatment.  Management of cardiac failure may require permanent discontinuation of therapy.  Care should be taken when using this agent in patients at risk.', '2', '', 'DDInter', 0);
