INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16406, 20843, 'Axitinib', 'Peptic Ulcer Perforation', 'Therapy with axitinib should be used with caution in patients who may be at increased risk for gastrointestinal perforation, such as those with a history of diverticulitis.  Patients presenting with new onset of abdominal symptoms should be evaluated promptly for early identification of gastrointestinal perforation.  It is recommended to monitor for symptoms of gastrointestinal perforation or fistula periodically throughout the treatment.', '2', '', 'DDInter', 0);
