INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16408, 20843, 'Axitinib', 'Hepatic Insufficiency', 'The systemic exposure to axitinib is higher in subjects with moderate hepatic impairment.  Dose adjustment is recommended in patients with moderate hepatic impairment.  No dose adjustment is required in patients with mild hepatic impairment.  The use of axitinib has not been studied in subjects with severe hepatic impairment.  It is recommended to monitor liver enzymes and bilirubin before the start of therapy and periodically thereafter.', '2', '', 'DDInter', 0);
