INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16412, 20843, 'Axitinib', 'Proteinuria', 'Proteinuria has been reported in patients taking axitinib.  It is recommended to monitor for proteinuria before initiation of therapy, and periodically thereafter.  Reduce dose or temporarily interrupt therapy in patients who develop moderate to severe proteinuria.', '2', '', 'DDInter', 0);
