INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16416, 20843, 'Axitinib', 'Posterior Leukoencephalopathy Syndrome', 'Reversible posterior leukoencephalopathy syndrome (RPLS) has been reported in patients receiving axitinib.  Consider this syndrome in any patient presenting with seizures, headache, visual disturbances, confusion or altered mental function.  It is recommended to discontinue therapy in patients developing RPLS.  The safety of reinitiating therapy in patients previously experiencing RPLS is not known.  Care and close monitoring should be considered in these patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16417, 20844, 'Axitinib', 'Posterior Leukoencephalopathy Syndrome', 'Reversible posterior leukoencephalopathy syndrome (RPLS) has been reported in patients receiving axitinib.  Consider this syndrome in any patient presenting with seizures, headache, visual disturbances, confusion or altered mental function.  It is recommended to discontinue therapy in patients developing RPLS.  The safety of reinitiating therapy in patients previously experiencing RPLS is not known.  Care and close monitoring should be considered in these patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16418, 20843, 'Axitinib', 'Thromboembolism', 'Arterial and venous thromboembolic events, including transient ischemic attack, cerebrovascular accident, myocardial infarction, pulmonary embolism, deep vein thrombosis, retinal occlusion, vein thrombosis, and deaths have been reported with the use of axitinib.  Use with caution in patients who are at risk for, or who have a history of, these events.  The use of axitinib has not been studied in patients who had an arterial or venous thromboembolic event within the previous 12 months.  It is recommended to monitor closely for signs and symptoms of thromboembolism.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16419, 20844, 'Axitinib', 'Thromboembolism', 'Arterial and venous thromboembolic events, including transient ischemic attack, cerebrovascular accident, myocardial infarction, pulmonary embolism, deep vein thrombosis, retinal occlusion, vein thrombosis, and deaths have been reported with the use of axitinib.  Use with caution in patients who are at risk for, or who have a history of, these events.  The use of axitinib has not been studied in patients who had an arterial or venous thromboembolic event within the previous 12 months.  It is recommended to monitor closely for signs and symptoms of thromboembolism.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16420, 20843, 'Axitinib', 'Hypothyroidism', 'Thyroid level fluctuations that include hypothyroidism and hyperthyroidism have been reported with the use of axitinib.  It is recommended to monitor thyroid function before initiation of, and periodically throughout treatment and to maintain euthyroid state according to standard medical practice.', '2', '', 'DDInter', 0);
