INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16456, 24220, 'Pimozide', 'Pituitary Neoplasms', 'Pimozide may have a tumorigenic potential and has been shown to cause a dose-related increase in pituitary tumors in mice.  When treated for up to 18 months, pituitary gland changes that occurred were characterized as hyperplasia at doses approximating the human dose and adenoma at doses about 15 times the maximum recommended human dose on a mg/kg basis.  The full significance of this finding is unknown but should be considered when prescribing pimozide, particularly in patients with preexisting pituitary tumors.', '3', '', 'DDInter', 0);
