INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16601, 24947, 'Pioglitazone', 'Anovulation', 'In premenopausal, anovulatory patients with insulin resistance, treatment with thiazolidinediones may result in resumption of ovulation.  Due to improved insulin sensitivity, pregnancy can occur if adequate contraception is not used.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (16602, 121, 'Pioglitazone', 'Weight Gain', 'Thiazolidinediones can cause dose-related weight gain, which may be undesirable in obese patients attempting to lose weight.  The mechanism of weight gain is unclear but probably involves a combination of fluid retention and fat accumulation.  In postmarketing experience with rosiglitazone, there have been reports of unusually rapid increases in weight, greater than those generally observed in clinical trials.  Patients who experience such increases should be assessed for fluid retention and volume-related events such as excessive edema and congestive heart failure.', '2', '', 'DDInter', 0);
