INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17101, 27835, 'Polyethylene glycol (3350 with electrolytes)', 'Water-Electrolyte Imbalance', 'Alkalinizing agents containing sodium can induce sodium and water retention and result in hypernatremia, hypokalemia, hyperosmolality, edema and aggravation of congestive heart failure.  Therapy with sodium- containing alkalinizing agents should not be used in patients with hypernatremia or fluid retention.  Clinical monitoring of acid/base balance and electrolytes prior to, during, a following completion of therapy with alkalinizing agents is recommended.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17102, 3788, 'Polyethylene glycol (3350 with electrolytes)', 'Diarrhea', 'Potassium chloride liquid suspension contains the stool softener, docusate sodium, as a dispersing agent.  Clinical studies with potassium chloride liquid suspension indicate that minor changes in stool consistency may be common though usually well tolerated.  However, patients may rarely experience diarrhea or cramping abdominal pain.  Patients with severe or chronic diarrhea or who are dehydrated ordinarily should not be prescribed potassium chloride liquid suspension.', '3', '', 'DDInter', 0);
