INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17256, 3595, 'Potassium acetate', 'Alkalosis', 'Hypokalemia in patients with metabolic or respiratory alkalosis should generally be treated with potassium chloride rather than an alkalinizing potassium salt (i.e. acetate, bicarbonate, citrate, or gluconate), since alkali therapy may exacerbate the condition.  In addition, hypochloremia may accompany alkalosis, which is best treated with potassium chloride.  Close monitoring of acid-base balance, serum electrolytes, electrocardiogram, and clinical status is recommended.', '2', '', 'DDInter', 0);
