INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17333, 2503, 'Potassium chloride', 'Diarrhea', 'Potassium chloride liquid suspension contains the stool softener, docusate sodium, as a dispersing agent.  Clinical studies with potassium chloride liquid suspension indicate that minor changes in stool consistency may be common though usually well tolerated.  However, patients may rarely experience diarrhea or cramping abdominal pain.  Patients with severe or chronic diarrhea or who are dehydrated ordinarily should not be prescribed potassium chloride liquid suspension.', '3', '', 'DDInter', 0);
