INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17791, 6705, 'Potassium citrate', 'Acid-Base Imbalance', 'Alkalinizing agents act as proton acceptors and/or dissociate to provide bicarbonate ions.  Elimination of bicarbonate is decreased in patients with renal impairment and can result in metabolic alkalosis.  Symptoms of metabolic alkalosis include hyperirritability or tetany, arrhythmia, and/or seizures (altered pH = altered calcium), or lactic acidosis due to impaired oxygen release.  Therapy with alkalinizing agents should be administered with extreme caution in patients with compromised renal function.  Clinical monitoring of renal function, acid/base balance and electrolytes is recommended.', '2', '', 'DDInter', 0);
