INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17813, 1190, 'Azathioprine', 'Bone Marrow Failure Disorders', 'Azathioprine may cause severe leukopenia, thrombocytopenia, macrocytic anemia, severe bone marrow depression and, rarely, pure red cell aplasia.  Therapy with azathioprine should be administered cautiously in patients with bone marrow depression.  Hematological toxicities are dose related.  Caution and routine monitoring of blood counts and platelets is recommended.  Patients should report any unusual bleeding or bruising.', '3', '', 'DDInter', 0);
