INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17825, 1190, 'Azathioprine', 'Infections', 'Serious infections, including reactivation of latent infections have been reported with the use of azathioprine.  Cases of JC virus-associated infection resulting in progressive multifocal leukoencephalopathy (PML), sometimes fatal, have been reported in patients treated with immunosuppressants, including azathioprine.  A diagnosis of PML should be considered in any patient presenting with new-onset neurological manifestations and a consultation with a neurologist is recommended.  Consider reducing the amount of immunosuppression in patients who develop PML, and in transplant patients, consider the risk that the reduced immunosuppression represents to the graft.', '3', '', 'DDInter', 0);
