INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17863, 2559, 'Acetazolamide', 'Bone Marrow Failure Disorders', 'The use of carbonic anhydrase inhibitors may rarely cause bone marrow suppression and blood dyscrasias at recommended dosages.  Aplastic anemia, thrombocytopenia or thrombocytopenia purpura, leukopenia, agranulocytosis, and hemolytic anemia have been reported.  Extreme caution should be exercised if carbonic anhydrase inhibitors are administered to patients with these preexisting conditions.  A baseline CBC and platelet count is recommended, as well as monitoring at regular intervals during therapy.', '3', '', 'DDInter', 0);
