INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17865, 2559, 'Acetazolamide', 'Adrenal Insufficiency', 'The use of carbonic anhydrase inhibitors is contraindicated in patients with adrenal insufficiency.  The carbonic anhydrase inhibitor effect may exacerbate electrolyte problems that can occur with primary adrenal insufficiency.  Extreme caution should be exercised if a carbonic anhydrase inhibitor is administered to these patients.  Monitoring electrolyte levels is recommended.', '3', '', 'DDInter', 0);
