INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17879, 2559, 'Acetazolamide', 'Diabetes Mellitus', 'Elevation of blood glucose, possibly caused by hypokalemia, and glycosuria have been reported rarely in diabetics receiving carbonic anhydrase inhibitors.  Therapy with carbonic anhydrase inhibitors should be administered cautiously in patients with diabetes mellitus.  Routine glucose monitoring is recommended during therapy.', '2', '', 'DDInter', 0);
