INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17881, 2559, 'Acetazolamide', 'Gout', 'Uric acid excretion is decreased during therapy with carbonic anhydrase inhibitors, and gout may be exacerbated.  Therapy with carbonic anhydrase inhibitors should be administered cautiously in patients with gout.  Elevated serum uric acid levels return to normal when the drug is discontinued.  Monitoring of uric acid levels is recommended in these patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17882, 3601, 'Acetazolamide', 'Gout', 'Uric acid excretion is decreased during therapy with carbonic anhydrase inhibitors, and gout may be exacerbated.  Therapy with carbonic anhydrase inhibitors should be administered cautiously in patients with gout.  Elevated serum uric acid levels return to normal when the drug is discontinued.  Monitoring of uric acid levels is recommended in these patients.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17883, 2559, 'Acetazolamide', 'Acidosis', 'Reduced plasma bicarbonate levels and, in some instances, elevated plasma chloride levels may result in metabolic acidosis during long-term therapy with carbonic anhydrase inhibitors.  Therapy with carbonic anhydrase inhibitors should be administered cautiously in patients with metabolic or hyperchloremic acidosis or with conditions that predispose to acidosis (renal disease, severe respiratory disorders, diarrhea).  The measurement of baseline and periodic serum bicarbonate is recommended.  If metabolic acidosis develops (it may be corrected by administration of sodium bicarbonate), and persists, a dose reduction or treatment discontinuation should be considered.', '2', '', 'DDInter', 0);
