INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17901, 21898, 'Pravastatin', 'Kidney Diseases', 'Pravastatin is partially eliminated by the kidney.  Drug and/or metabolite accumulation may occur in patients with significant renal impairment, which may increase the risk of adverse effects, including hepatic and musculoskeletal toxicities.  Therapy with pravastatin should be administered cautiously in patients with severely impaired renal function.  A lower initial dosage may be appropriate, accompanied by close clinical monitoring.', '3', '', 'DDInter', 0);
