INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (17937, 13086, 'Pramipexole', 'Kidney Failure, Chronic', 'Pramipexole is eliminated by the kidneys, and approximately 90% of the drug is excreted unchanged in the urine.  Renal insufficiency decreases the renal clearance and increases the half-life.  Therapy with pramipexole should be administered cautiously and at a reduced dose in patients with compromised renal function.  Pramipexole clearance is extremely low in dialysis patients, as a negligible amount of the drug is removed by it.', '3', '', 'DDInter', 0);
