INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (18601, 6080, 'Pregabalin', 'Cardiac Conduction System Disease', 'Pregabalin should be administered cautiously in patients with preexisting conduction abnormalities (e.g., marked first-degree AV block or second- or third-degree AV block), as it has been associated with PR interval prolongation.  In a clinical trial, the mean PR interval increase was 3 to 6 msec with pregabalin at doses higher than or equal to 300 mg per day.  This mean change difference was not associated with an increased risk of PR increase >=25% from baseline, an increased percentage of subjects with on-treatment PR >200 msec, or an increased risk of adverse reactions of second or third degree AV block.  Subgroup analyses did not identify an increased risk of PR prolongation in patients with baseline PR prolongation or in patients taking other PR prolonging medications.  However, these analyses cannot be considered definitive because of the limited number of patients in these categories.', '2', '', 'DDInter', 0);
