INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (18694, 31135, 'Procyclidine', 'Heart Diseases', 'Anticholinergics block vagal inhibition of the SA nodal pacemaker.  Therapy with anticholinergics should be administered cautiously to patients with tachycardia, congestive heart failure, or coronary artery disease.  Premature ventricular depolarization, ventricular tachycardia, and fibrillation associated with anticholinergics are rare.', '2', '', 'DDInter', 0);
