INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19368, 854, 'Prednisolone', 'Peptic Ulcer', 'Corticosteroids may cause peptic ulcer disease and gastrointestinal (GI) hemorrhage, usually when given in high dosages or for prolonged periods.  However, even conventional dosages may aggravate symptoms in patients with a history of peptic ulcers.  Delayed healing of ulcers has also been reported.  Therapy with corticosteroids should be avoided or administered cautiously in patients with active or latent peptic ulcers or other risk factors for GI bleeding.  Some clinicians recommend the use of prophylactic antacids or H2-antagonists between meals when large doses of corticosteroids are necessary.', '2', '', 'DDInter', 0);
