INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19536, 3156, 'Progesterone', 'Liver Diseases', 'The use of progestogens, in general, is contraindicated in patients with impaired hepatic function or liver disease.  There are little or no data concerning the pharmacokinetic disposition of the different progestogens in patients with hepatic disease.  However, most hormones, including progestational hormones, are known to be extensively metabolized by the liver.  Medroxyprogesterone should not be used by women with significant liver disease and should be discontinued if jaundice or disturbances of liver function occur.', '3', '', 'DDInter', 0);
