INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19596, 3156, 'Progesterone', 'Water-Electrolyte Imbalance', 'Estrogens and progestogens may cause fluid retention, particularly when given in high dosages or for prolonged periods.  Therapy with these agents should be administered cautiously in patients who have preexisting problems with excess fluid.  In addition, patients with conditions that may be adversely affected by fluid accumulation, such as asthma, epilepsy, migraine, and cardiovascular or renal dysfunction, should be observed for exacerbation of their condition during estrogen and/or progestogen therapy.', '2', '', 'DDInter', 0);
