INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19722, 23721, 'Propafenone', 'Heart Failure', 'The use of propafenone is contraindicated in patients with uncontrolled congestive heart failure, cardiogenic shock, bradycardia, or marked hypotension.  Propafenone can exacerbate congestive heart failure by beta blockade of sympathetic stimulation necessary for circulatory support.  Propafenone also has negative inotropic activity that can depress cardiac output and cardiac index.', '3', '', 'DDInter', 0);
