INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19731, 23721, 'Propafenone', 'Lupus Erythematosus, Systemic', 'Reversible (+) ANA titers have been reported during administration of propafenone.  Therapy with propafenone should be administered cautiously in patients with established systemic lupus erythematosus (SLE).  Clinical monitoring, including ANA titers, is recommended.  Patients without a previous history of (+) ANA titer or SLE who develop a (+) ANA while receiving propafenone should be evaluated and, if ANA titers persist or worsen, discontinuation of therapy should be considered.', '2', '', 'DDInter', 0);
