INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19776, 3728, 'Propofol', 'Cardiomyopathies', 'Propofol can induce severe hypotension and/or cardiovascular depression, particularly during the induction phase of sedation or anesthesia.  Use of propofol infusions for both adult and pediatric ICU sedation has been associated with a constellation of metabolic derangements and organ system failures, referred to as Propofol Infusion Syndrome, that have resulted in death.  The syndrome is characterized by severe metabolic acidosis, hyperkalemia, lipemia, rhabdomyolysis,hepatomegaly, renal failure, ECG changes (ST segment elevation similar to that in Brugada syndrome) and/or cardiac failure.  Therapy with propofol should be administered cautiously in patients with preexisting cardiovascular disorders, hemodynamic impairment, increased intracranial pressure, or impaired cerebral circulation.', '3', '', 'DDInter', 0);
