INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (19850, 6445, 'Propranolol', 'Hypersensitivity', 'The use of beta-adrenergic receptor blocking agents (aka beta-blockers) in patients with a history of allergic reactions or anaphylaxis may be associated with heightened reactivity to culprit allergens.  The frequency and/or severity of attacks may be increased during beta-blocker therapy.  In addition, these patients may be refractory to the usual doses of epinephrine used to treat acute hypersensitivity reactions and may require a beta-agonist such as isoproterenol.', '3', '', 'DDInter', 0);
