INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (20015, 3365, 'Pseudoephedrine', 'Intestinal Obstruction', 'The extended-release formulation of pseudoephedrine (Sudafed 24 Hour) contains a non-deformable material.  There have been rare reports of obstructive symptoms in patients with known strictures following the ingestion of similar sustained-release products.  Therapy with the extended-release formulation of pseudoephedrine should be administered cautiously in patients with preexisting severe gastrointestinal narrowing or obstruction, whether pathologic or iatrogenic.', '2', '', 'DDInter', 0);
