INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (20018, 3365, 'Pseudoephedrine', 'Phenylketonurias', 'Chewable products frequently may contain aspartame, which is metabolized in the gastrointestinal tract to phenylalanine.  Sudafed (brand of pseudoephedrine) chewable 15 mg tablets provide the equivalent of 0.78 mg of phenylalanine per each tablet.  The aspartame/phenylalanine content should be considered when this and similar products are used in patients who must restrict their intake of phenylalanine (i.e. phenylketonurics).', '2', '', 'DDInter', 0);
