INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (20901, 4532, 'Quetiapine', 'Cataract', 'Prolonged use of quetiapine was associated with the development of cataracts in dogs.  Lens changes have also been observed in humans during chronic treatment with quetiapine, but a causal relationship has not been established.  Long-term therapy with quetiapine should be administered cautiously in patients with a history of cataracts.  Examination of the lens by slit lamp exam or other appropriately sensitive methods is recommended at initiation of treatment or shortly thereafter and at 6-month intervals during chronic treatment.', '2', '', 'DDInter', 0);
