INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (20995, 1191, 'Quetiapine', 'Hypothyroidism', 'During clinical trials, the use of quetiapine was associated with a dose-related decrease in total and free thyroxine (T4) levels that reached approximately 20% at the higher end of the therapeutic dose range and peaked within the first 2 to 4 weeks of treatment.  Generally, the changes were of no clinical significance and were reversible following discontinuation of quetiapine regardless of the duration of treatment.  TBG levels were not altered in any patient, while TSH increased in 0.4% (10/2386) of patients, some of whom required thyroid replacement therapy.  Therapy with quetiapine should be administered cautiously in patients with thyroid disease.  Closer monitoring of thyroid function may be appropriate following initiation or cessation of quetiapine.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (20996, 1630, 'Quetiapine', 'Hypothyroidism', 'During clinical trials, the use of quetiapine was associated with a dose-related decrease in total and free thyroxine (T4) levels that reached approximately 20% at the higher end of the therapeutic dose range and peaked within the first 2 to 4 weeks of treatment.  Generally, the changes were of no clinical significance and were reversible following discontinuation of quetiapine regardless of the duration of treatment.  TBG levels were not altered in any patient, while TSH increased in 0.4% (10/2386) of patients, some of whom required thyroid replacement therapy.  Therapy with quetiapine should be administered cautiously in patients with thyroid disease.  Closer monitoring of thyroid function may be appropriate following initiation or cessation of quetiapine.', '2', '', 'DDInter', 0);
