INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21255, 27447, 'Azilsartan medoxomil', 'Hyperkalemia', 'Drugs that inhibit the renin-angiotensin, such as angiotensin II receptor antagonist system can cause hyperkalemia.  Concomitant use of these agents with drugs that increase potassium levels may increase the risk of hyperkalemia.  Use caution when using these agents together and monitor serum potassium periodically.', '2', '', 'DDInter', 0);
