INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (21801, 1203, 'Azithromycin', 'Liver Diseases', 'Azithromycin is contraindicated in patients with a history of cholestatic jaundice/hepatic dysfunction associated with prior use of azithromycin.  Azithromycin is primarily eliminated by biliary excretion; however, the pharmacokinetics of azithromycin have not been established in patients with liver dysfunction.  Therapy with azithromycin should be administered cautiously in patients with liver and/or biliary disease.  Abnormal liver function, hepatitis, cholestatic jaundice, hepatic necrosis, and hepatic failure have been reported, with some fatal cases.  Treatment should be discontinued immediately if signs/symptoms of hepatitis occur.', '3', '', 'DDInter', 0);
